// SPDX-License-Identifier: MIT

pragma solidity 0.6.12;

interface ISEP20Native {
    function name() external view returns (string memory);

    function symbol() external view returns (string memory);

    function decimals() external view returns (uint256);

    function totalSupply() external returns (uint256);

    function balanceOf(address _account) external returns (uint256);

    function transfer(address _to, uint256 _value) external returns (bool);

    function transferFrom(address _from, address _to, uint256 _value) external returns (bool);

    function approve(address _spender, uint256 _value) external returns (bool);

    function allowance(address _owner, address _spender) external returns (uint256);

    function increaseAllowance(address _spender, uint256 _delta) external returns (bool);

    function decreaseAllowance(address _spender, uint256 _delta) external returns (bool);
}

contract WBCH is ISEP20Native {
    // https://docs.smartbch.org/smartbch/smartbch-evolution-proposals-seps/sep-206
    ISEP20Native native = ISEP20Native(0x0000000000000000000000000000000000002711);

    function name() external view override returns (string memory) {
        return "BCH";
    }

    function symbol() external view override returns (string memory) {
        return "BCH";
    }

    function decimals() external view override returns (uint256) {
        return 18;
    }

    function totalSupply() external override returns (uint256) {
        return native.totalSupply();
    }

    function balanceOf(address _account) external override returns (uint256) {
        return native.balanceOf(_account);
    }

    function transfer(address _to, uint256 _value) external override returns (bool) {
        return native.transfer(_to, _value);
    }

    function transferFrom(address _from, address _to, uint256 _value) external override returns (bool) {
        return native.transferFrom(_from, _to, _value);
    }

    function approve(address _spender, uint256 _value) external override returns (bool) {
        return native.approve(_spender, _value);
    }

    function allowance(address _owner, address _spender) external override returns (uint256) {
        return native.allowance(_owner, _spender);
    }

    function increaseAllowance(address _spender, uint256 _delta) external override returns (bool) {
        return native.increaseAllowance(_spender, _delta);
    }

    function decreaseAllowance(address _spender, uint256 _delta) external override returns (bool) {
        return native.decreaseAllowance(_spender, _delta);
    }
}
